mapper.select:
select a.id, a.art_id, a.parent_id, a.level, a.user_id, a.content, a.ctime, a.status,b.user_id,b.user_nickname,b.user_img,b.user_sex
,b.user_mobile,b.user_email,b.user_ch_name,b.user_en_name
from DTKQ.article_comment a
left join user b on a.user_id =b.user_id
where 1=1

        and id = #{id}


        and art_id = #{artId}


        and parent_id = #{parentId}


        and level = #{level}


        and user_id = #{userId}


        and content = #{content}


        and ctime = #{ctime}


        and status = #{status}

limit #{offset}, #{limit}

select count(*)
from DTKQ.article_comment a
where 1=1

    and id = #{id}


    and art_id = #{artId}


    and parent_id = #{parentId}


    and level = #{level}


    and user_id = #{userId}


    and content = #{content}


    and ctime = #{ctime}


    and status = #{status}
