mapper.select:
select
a.id, a.ask_id, a.user_id, a.title, a.content, a.ctime, a.status,a.thumb_num
,b.user_id,b.user_nickname,b.user_img,b.user_sex
,b.user_mobile,b.user_email,b.user_ch_name,b.user_en_name
from DTKQ.ask_answer a
LEFT JOIN `user` b on a.user_id= b.user_id


        and a.id = #{id}


        and a.ask_id = #{askId}


        and a.user_id = #{userId}


        and a.title = #{title}


        and a.content = #{content}


        and a.ctime = #{ctime}


        and a.status = #{status}


limit #{offset}, #{limit}

mapper.update:
update DTKQ.ask_answer


        thumb_num = thumb_num +1


where id = #{id}

mapper.select:
select
count(*)
from DTKQ.ask_answer a
LEFT JOIN `user` b on a.user_id= b.user_id



        and a.id = #{id}


        and a.ask_id = #{askId}


        and a.user_id = #{userId}


        and a.title = #{title}


        and a.content = #{content}


        and a.ctime = #{ctime}


        and a.status = #{status}
