mapper.update:
update class_obj
set obj_id = #{objId,jdbcType=INTEGER}

  ,user_id = #{userId,jdbcType=INTEGER}


  ,title = #{title,jdbcType=INTEGER}


  ,url = #{url,jdbcType=VARCHAR}


  ,img = #{img,jdbcType=VARCHAR}


  ,status = #{status,jdbcType=VARCHAR}

where obj_id = #{objId,jdbcType=INTEGER}

mapper.select:
select a.obj_id, a.user_id, a.title,b.class_id, a.url, a.img, a.status,a.ctime from class_obj a left join link_class_obj b on a.obj_id=b.obj_id where 1=1
and b.obj_id = #{objId,jdbcType=INTEGER}
and b.class_id = #{linkClassId,jdbcType=INTEGER}
and a.status=0 limit 6

select a.obj_id, a.user_id, a.title, a.url, a.img, a.status,a.ctime,b.class_id from class_obj a left join link_class_obj b on b.obj_id = a.obj_id where a.status=0
and b.class_id = #{linkClassId}
limit #{offset}, #{limit}
