mapper.select:

select id, `level`, parent_id, ch_name, en_name, auth, ctime, `style`, `status`
from talk_class
where id = #{id,jdbcType=INTEGER}

select id, `level`, parent_id, ch_name, en_name, auth, ctime, `style`, `status`
from talk_class
where 1=1

  and level=#{level,jdbcType=VARCHAR}


 and parent_id=#{parentId,jdbcType=INTEGER}


  and style=#{style,jdbcType=INTEGER}

order by  auth asc

