mapper.select:
select
  user_id, user_ch_name, user_en_name, user_nickname, user_img, user_sex, user_mobile, user_email, ctime, auth, status
from DTKQ.user


        and user_id = #{userId}


        and user_ch_name = #{userChName}


        and user_en_name = #{userEnName}


        and user_nickname = #{userNickname}


        and user_img = #{userImg}


        and user_sex = #{userSex}


        and user_mobile = #{userMobile}


        and user_email = #{userEmail}


        and password = #{password}


        and ctime = #{ctime}


        and auth = #{auth}


        and status = #{status}


limit #{offset}, #{limit}
